{	"version": 3,	"file": "main.css",	"sources": [		"main.scss",		"_sass/_main.scss",		"_sass/base/_reset.scss",		"_sass/base/_variables.scss",		"_sass/base/_root.scss",		"_sass/base/_typography.scss",		"_sass/base/_base.scss",		"_sass/components/_header.scss",		"_sass/components/_footer.scss",		"_sass/components/_sidebar.scss",		"_sass/components/_search.scss",		"_sass/components/_buttons.scss",		"_sass/components/_tags.scss",		"_sass/components/_code.scss",		"_sass/components/_tables.scss",		"_sass/components/_lists.scss",		"_sass/components/_steps.scss",		"_sass/components/_toc.scss",		"_sass/components/_breadcrumbs.scss",		"_sass/components/_page-nav.scss",		"_sass/components/_alerts.scss",		"_sass/components/_animations.scss",		"_sass/layouts/_default.scss",		"_sass/layouts/_doc.scss",		"_sass/layouts/_page.scss",		"_sass/themes/_palettes.scss",		"_sass/themes/_dark-mode.scss",		"_sass/utilities/_animations.scss",		"_sass/utilities/_responsive.scss",		"_sass/utilities/_helpers.scss"	],	"sourcesContent": [		"@import "main";",		"// Main SCSS file that imports all partials

// Base
@import "base/reset";
@import "base/variables";
@import "base/root";
@import "base/typography";
@import "base/base";

// Components
@import "components/header";
@import "components/footer";
@import "components/sidebar";
@import "components/search";
@import "components/buttons";
@import "components/tags";
@import "components/code";
@import "components/tables";
@import "components/lists";
@import "components/steps";
@import "components/toc";
@import "components/breadcrumbs";
@import "components/page-nav";
@import "components/alerts";
@import "components/animations";

// Layouts
@import "layouts/default";
@import "layouts/doc";
@import "layouts/page";

// Themes
@import "themes/palettes";
@import "themes/dark-mode";

// Utilities
@import "utilities/animations";
@import "utilities/responsive";
@import "utilities/helpers";",		"// CSS Reset and Base Styles

*,
*::before,
*::after {
  box-sizing: border-box;
}

* {
  margin: 0;
  padding: 0;
}

html {
  -webkit-text-size-adjust: 100%;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-rendering: optimizeLegibility;
}

body {
  min-height: 100vh;
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
}

img,
picture,
video,
canvas,
svg {
  display: block;
  max-width: 100%;
}

input,
button,
textarea,
select {
  font: inherit;
}

p,
h1,
h2,
h3,
h4,
h5,
h6 {
  overflow-wrap: break-word;
}

a {
  color: inherit;
  text-decoration: none;
}

button {
  background: none;
  border: none;
  cursor: pointer;
}

ul,
ol {
  list-style: none;
}

#root,
#__next {
  isolation: isolate;
}",		"// Base Variables

// Typography
$font-family-base: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
$font-family-mono: 'Fira Code', 'SF Mono', Monaco, 'Inconsolata', 'Fira Mono', monospace;

$font-size-base: 16px;
$line-height-base: 1.6;

$font-sizes: (
  'xs': 0.75rem,
  'sm': 0.875rem,
  'base': 1rem,
  'lg': 1.125rem,
  'xl': 1.25rem,
  '2xl': 1.5rem,
  '3xl': 1.875rem,
  '4xl': 2.25rem,
  '5xl': 3rem
);

$font-weights: (
  'light': 300,
  'normal': 400,
  'medium': 500,
  'semibold': 600,
  'bold': 700,
  'extrabold': 800
);

// Spacing
$spacer: 1rem;
$spacers: (
  0: 0,
  1: $spacer * 0.25,
  2: $spacer * 0.5,
  3: $spacer * 0.75,
  4: $spacer,
  5: $spacer * 1.25,
  6: $spacer * 1.5,
  8: $spacer * 2,
  10: $spacer * 2.5,
  12: $spacer * 3,
  16: $spacer * 4,
  20: $spacer * 5,
  24: $spacer * 6
);

// Layout
$container-max-width: 1280px;
$sidebar-width: 280px;
$header-height: 64px;

// Borders
$border-radius-sm: 4px;
$border-radius: 8px;
$border-radius-lg: 12px;
$border-radius-xl: 16px;
$border-radius-full: 9999px;

// Shadows
$shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
$shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
$shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
$shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
$shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);

// Transitions
$transition-base: all 0.2s ease;
$transition-fast: all 0.15s ease;
$transition-slow: all 0.3s ease;

// Breakpoints
$breakpoints: (
  'sm': 640px,
  'md': 768px,
  'lg': 1024px,
  'xl': 1280px,
  '2xl': 1536px
);

// Z-index
$z-index: (
  'dropdown': 1000,
  'sticky': 1020,
  'fixed': 1030,
  'modal-backdrop': 1040,
  'modal': 1050,
  'popover': 1060,
  'tooltip': 1070
);",		"// Root CSS Custom Properties

:root {
  // Default colors (light mode)
  --color-bg: #ffffff;
  --color-bg-secondary: #f9fafb;
  --color-bg-tertiary: #f3f4f6;

  --color-text: #111827;
  --color-text-secondary: #4b5563;
  --color-text-tertiary: #9ca3af;

  --color-border: #e5e7eb;

  // Gray scale
  --color-gray-50: #f9fafb;
  --color-gray-100: #f3f4f6;
  --color-gray-200: #e5e7eb;
  --color-gray-300: #d1d5db;
  --color-gray-400: #9ca3af;
  --color-gray-500: #6b7280;
  --color-gray-600: #4b5563;
  --color-gray-700: #374151;
  --color-gray-800: #1f2937;
  --color-gray-900: #111827;

  // Success/Warning/Error colors
  --color-success: #10b981;
  --color-warning: #f59e0b;
  --color-error: #ef4444;

  // Applied when a color palette is selected
  &[data-palette] {
    // Primary colors come from _palettes.scss
  }

  // Dark mode overrides
  &[data-theme="dark"] {
    --color-bg: #0f172a;
    --color-bg-secondary: #1e293b;
    --color-bg-tertiary: #334155;

    --color-text: #f1f5f9;
    --color-text-secondary: #cbd5e1;
    --color-text-tertiary: #94a3b8;

    --color-border: #334155;

    // Dark mode gray scale
    --color-gray-50: #0f172a;
    --color-gray-100: #1e293b;
    --color-gray-200: #334155;
    --color-gray-300: #475569;
    --color-gray-400: #64748b;
    --color-gray-500: #94a3b8;
    --color-gray-600: #cbd5e1;
    --color-gray-700: #e2e8f0;
    --color-gray-800: #f1f5f9;
    --color-gray-900: #f8fafc;
  }
}

// Apply palette to body instead of :root for better specificity
body {
  // This ensures palette colors are available
  &[data-palette="blue"] {
    --color-primary-50: #eff6ff;
    --color-primary-100: #dbeafe;
    --color-primary-200: #bfdbfe;
    --color-primary-300: #93c5fd;
    --color-primary-400: #60a5fa;
    --color-primary-500: #3b82f6;
    --color-primary-600: #2563eb;
    --color-primary-700: #1d4ed8;
    --color-primary-800: #1e40af;
    --color-primary-900: #1e3a8a;
  }
}",		"// Typography styles

// Headings
h1, h2, h3, h4, h5, h6 {
  font-weight: map-get($font-weights, 'semibold');
  line-height: 1.25;
  color: var(--color-text);
  margin-bottom: map-get($spacers, 4);
}

h1 {
  font-size: map-get($font-sizes, '4xl');
  font-weight: map-get($font-weights, 'bold');
  letter-spacing: -0.025em;

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, '5xl');
  }
}

h2 {
  font-size: map-get($font-sizes, '3xl');
  font-weight: map-get($font-weights, 'bold');
  letter-spacing: -0.02em;
  margin-top: map-get($spacers, 8);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, '4xl');
  }
}

h3 {
  font-size: map-get($font-sizes, '2xl');
  margin-top: map-get($spacers, 6);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, '3xl');
  }
}

h4 {
  font-size: map-get($font-sizes, 'xl');
  margin-top: map-get($spacers, 6);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, '2xl');
  }
}

h5 {
  font-size: map-get($font-sizes, 'lg');
  margin-top: map-get($spacers, 4);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, 'xl');
  }
}

h6 {
  font-size: map-get($font-sizes, 'base');
  margin-top: map-get($spacers, 4);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, 'lg');
  }
}

// Paragraphs
p {
  margin-bottom: map-get($spacers, 4);

  &:last-child {
    margin-bottom: 0;
  }
}

// Lead text
.lead {
  font-size: map-get($font-sizes, 'lg');
  font-weight: map-get($font-weights, 'normal');
  line-height: 1.6;
  color: var(--color-text-secondary);

  @media (min-width: map-get($breakpoints, 'md')) {
    font-size: map-get($font-sizes, 'xl');
  }
}

// Small text
.small {
  font-size: map-get($font-sizes, 'sm');
}

// Strong
strong, b {
  font-weight: map-get($font-weights, 'semibold');
}

// Emphasis
em, i {
  font-style: italic;
}

// Blockquote
blockquote {
  border-left: 4px solid var(--color-primary-500);
  padding-left: map-get($spacers, 4);
  margin: map-get($spacers, 6) 0;
  font-style: italic;
  color: var(--color-text-secondary);

  p {
    margin-bottom: map-get($spacers, 2);

    &:last-child {
      margin-bottom: 0;
    }
  }

  cite {
    display: block;
    margin-top: map-get($spacers, 2);
    font-size: map-get($font-sizes, 'sm');
    font-style: normal;
    color: var(--color-text-tertiary);

    &::before {
      content: 'â€” ';
    }
  }
}

// Horizontal rule
hr {
  border: none;
  border-top: 1px solid var(--color-border);
  margin: map-get($spacers, 8) 0;
}

// Abbreviations
abbr[title] {
  text-decoration: underline dotted;
  cursor: help;
}

// Mark
mark {
  background-color: var(--color-primary-100);
  color: var(--color-primary-900);
  padding: 0.125em 0.25em;
  border-radius: $border-radius-sm;
}",		"// Base styles

html {
  font-size: $font-size-base;
  scroll-behavior: smooth;
}

body {
  font-family: $font-family-base;
  font-size: 1rem;
  line-height: $line-height-base;
  color: var(--color-text);
  transition: color $transition-base;
}

// Selection
::selection {
  background-color: var(--color-primary-200);
  color: var(--color-primary-900);
}

// Focus styles
:focus {
  outline: none;
}

:focus-visible {
  outline: 2px solid var(--color-primary-500);
  outline-offset: 2px;
}

// Container
.container {
  max-width: $container-max-width;
  margin: 0 auto;
  padding: 0 map-get($spacers, 4);

  @media (min-width: map-get($breakpoints, 'md')) {
    padding: 0 map-get($spacers, 6);
  }

  @media (min-width: map-get($breakpoints, 'lg')) {
    padding: 0 map-get($spacers, 8);
  }
}

// Links
a {
  color: var(--color-primary-600);
  transition: color $transition-fast;

  &:hover {
    color: var(--color-primary-700);
  }

  &:focus-visible {
    outline: 2px solid var(--color-primary-500);
    outline-offset: 2px;
    border-radius: $border-radius-sm;
  }
}

// Images
img {
  height: auto;
  border-radius: $border-radius;
}

// Code - moved to components/_code.scss for better organization
// Keeping only the pre code reset here
pre code {
  background-color: transparent;
  padding: 0;
}

// Scrollbar
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--color-bg-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--color-gray-400);
  border-radius: $border-radius-full;

  &:hover {
    background: var(--color-gray-500);
  }
}

// Utility classes
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}",		"// Header styles

.site-header {
  position: sticky;
  top: 0;
  z-index: map-get($z-index, 'sticky');
  background-color: var(--color-bg);
  border-bottom: 1px solid var(--color-border);
  backdrop-filter: blur(10px);
  background-color: var(--color-bg);
  opacity: 0.95;

  .header-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: $header-height;
    max-width: $container-max-width;
    margin: 0 auto;
    padding: 0 map-get($spacers, 4);

    @media (min-width: map-get($breakpoints, 'lg')) {
      padding: 0 map-get($spacers, 6);
    }
  }
}

.site-logo {
  font-size: map-get($font-sizes, 'xl');
  font-weight: map-get($font-weights, 'bold');

  a {
    color: var(--color-text);
    display: flex;
    align-items: center;
    gap: map-get($spacers, 2);

    &:hover {
      color: var(--color-primary-600);
    }
  }
}

.site-nav {
  display: none;

  @media (min-width: map-get($breakpoints, 'md')) {
    display: block;
  }

  .nav-list {
    display: flex;
    gap: map-get($spacers, 6);

    a {
      color: var(--color-text-secondary);
      font-weight: map-get($font-weights, 'medium');
      position: relative;
      padding: map-get($spacers, 2) 0;

      &:hover {
        color: var(--color-text);
      }

      &.active {
        color: var(--color-primary-600);

        &::after {
          content: '';
          position: absolute;
          bottom: -1px;
          left: 0;
          right: 0;
          height: 2px;
          background-color: var(--color-primary-600);
        }
      }
    }
  }
}

.header-actions {
  display: flex;
  align-items: center;
  gap: map-get($spacers, 3);
}

// Icon buttons
.search-toggle,
.theme-toggle,
.palette-toggle,
.mobile-menu-toggle {
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: $border-radius;
  color: var(--color-text-secondary);
  transition: all $transition-fast;

  &:hover {
    background-color: var(--color-bg-secondary);
    color: var(--color-text);
  }

  &:focus-visible {
    outline: 2px solid var(--color-primary-500);
    outline-offset: 2px;
  }
}

// Theme toggle
.theme-toggle {
  .theme-icon-light {
    display: block;
  }

  .theme-icon-dark {
    display: none;
  }

  [data-theme="dark"] & {
    .theme-icon-light {
      display: none;
    }

    .theme-icon-dark {
      display: block;
    }
  }
}

// Palette selector
.palette-selector {
  position: relative;
}

.palette-dropdown {
  position: absolute;
  top: calc(100% + map-get($spacers, 2));
  right: 0;
  background-color: var(--color-bg);
  border: 1px solid var(--color-border);
  border-radius: $border-radius-lg;
  padding: map-get($spacers, 3);
  box-shadow: $shadow-lg;
  opacity: 0;
  visibility: hidden;
  transform: translateY(-10px);
  transition: all $transition-fast;

  .palette-selector.active & {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
  }
}

.palette-grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: map-get($spacers, 2);
}

.palette-option {
  width: 32px;
  height: 32px;
  border-radius: $border-radius;
  overflow: hidden;
  position: relative;
  transition: transform $transition-fast;

  &:hover {
    transform: scale(1.1);
  }

  &:focus-visible {
    outline: 2px solid var(--color-primary-500);
    outline-offset: 2px;
  }
}

.palette-preview {
  width: 100%;
  height: 100%;
  display: block;

  &.palette-blue { background: linear-gradient(135deg, #3b82f6, #1d4ed8); }
  &.palette-purple { background: linear-gradient(135deg, #a855f7, #7c3aed); }
  &.palette-green { background: linear-gradient(135deg, #22c55e, #15803d); }
  &.palette-orange { background: linear-gradient(135deg, #f97316, #c2410c); }
  &.palette-red { background: linear-gradient(135deg, #ef4444, #b91c1c); }
  &.palette-teal { background: linear-gradient(135deg, #14b8a6, #0f766e); }
  &.palette-pink { background: linear-gradient(135deg, #ec4899, #be185d); }
  &.palette-indigo { background: linear-gradient(135deg, #6366f1, #4338ca); }
  &.palette-amber { background: linear-gradient(135deg, #f59e0b, #b45309); }
  &.palette-slate { background: linear-gradient(135deg, #64748b, #334155); }
}

// Mobile menu toggle
.mobile-menu-toggle {
  display: flex;
  flex-direction: column;
  gap: 4px;

  @media (min-width: map-get($breakpoints, 'md')) {
    display: none;
  }

  span {
    width: 20px;
    height: 2px;
    background-color: currentColor;
    transition: all $transition-fast;
  }

  &.active {
    span:nth-child(1) {
      transform: translateY(6px) rotate(45deg);
    }

    span:nth-child(2) {
      opacity: 0;
    }

    span:nth-child(3) {
      transform: translateY(-6px) rotate(-45deg);
    }
  }
}",		"// Footer styles

.site-footer {
  background-color: var(--color-bg-secondary);
  border-top: 1px solid var(--color-border);
  margin-top: auto;

  .footer-container {
    max-width: $container-max-width;
    margin: 0 auto;
    padding: map-get($spacers, 6) map-get($spacers, 4);

    @media (min-width: map-get($breakpoints, 'lg')) {
      padding: map-get($spacers, 8) map-get($spacers, 6);
    }
  }

  .footer-content {
    display: grid;
    grid-template-columns: 1fr;
    gap: map-get($spacers, 6);

    @media (min-width: map-get($breakpoints, 'md')) {
      grid-template-columns: 2fr 1fr 1fr 1fr;
    }
  }

  .footer-section {
    h3 {
      font-size: map-get($font-sizes, 'lg');
      font-weight: map-get($font-weights, 'semibold');
      margin-bottom: map-get($spacers, 3);
      color: var(--color-text);
    }

    p {
      color: var(--color-text-secondary);
      margin-bottom: map-get($spacers, 3);
      line-height: 1.7;
    }

    ul {
      list-style: none;
      padding: 0;
      margin: 0;

      li {
        margin-bottom: map-get($spacers, 2);

        a {
          color: var(--color-text-secondary);
          font-size: map-get($font-sizes, 'sm');

          &:hover {
            color: var(--color-primary-600);
          }
        }
      }
    }
  }

  .footer-bottom {
    margin-top: map-get($spacers, 6);
    padding-top: map-get($spacers, 6);
    border-top: 1px solid var(--color-border);
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: map-get($spacers, 3);

    @media (min-width: map-get($breakpoints, 'md')) {
      flex-direction: row;
      justify-content: space-between;
    }

    p {
      margin: 0;
      color: var(--color-text-tertiary);
      font-size: map-get($font-sizes, 'sm');
    }
  }

  .footer-links {
    display: flex;
    gap: map-get($spacers, 4);

    a {
      color: var(--color-text-tertiary);
      font-size: map-get($font-sizes, 'sm');

      &:hover {
        color: var(--color-text-secondary);
      }
    }
  }

  .footer-social {
    display: flex;
    gap: map-get($spacers, 3);

    a {
      width: 40px;
      height: 40px;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: $border-radius-full;
      background-color: var(--color-bg);
      color: var(--color-text-secondary);
      transition: all $transition-fast;

      &:hover {
        background-color: var(--color-primary-600);
        color: white;
        transform: translateY(-2px);
      }
    }
  }
}",		"// Sidebar styles

.doc-sidebar {
  position: sticky;
  top: calc($header-height + map-get($spacers, 4));
  height: calc(100vh - $header-height - map-get($spacers, 8));
  width: $sidebar-width;
  overflow-y: auto;
  padding-right: map-get($spacers, 4);

  @media (max-width: map-get($breakpoints, 'lg')) {
    display: none;
  }

