}",		"// Button styles

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: map-get($spacers, 2) map-get($spacers, 4);
  font-size: map-get($font-sizes, 'base');
  font-weight: map-get($font-weights, 'medium');
  line-height: 1.5;
  border-radius: $border-radius;
  border: 2px solid transparent;
  cursor: pointer;
  transition: all $transition-fast;
  text-decoration: none;
  white-space: nowrap;
  user-select: none;

  &:focus-visible {
    outline: 2px solid var(--color-primary-500);
    outline-offset: 2px;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  // Primary button
  &.btn-primary {
    color: white;
    background-color: var(--color-primary-600);
    border-color: var(--color-primary-600);

    &:hover:not(:disabled) {
      background-color: var(--color-primary-700);
      border-color: var(--color-primary-700);
      transform: translateY(-1px);
      box-shadow: $shadow-md;
    }

    &:active:not(:disabled) {
      transform: translateY(0);
      box-shadow: $shadow-sm;
    }
  }

  // Secondary button
  &.btn-secondary {
    color: var(--color-primary-700);
    background-color: var(--color-primary-100);
    border-color: var(--color-primary-200);

    [data-theme="dark"] & {
      color: var(--color-primary-300);
      background-color: rgba(var(--color-primary-500-rgb), 0.2);
      border-color: rgba(var(--color-primary-500-rgb), 0.3);
    }

    &:hover:not(:disabled) {
      background-color: var(--color-primary-200);
      border-color: var(--color-primary-300);

      [data-theme="dark"] & {
        background-color: rgba(var(--color-primary-500-rgb), 0.3);
        border-color: rgba(var(--color-primary-500-rgb), 0.4);
      }
    }
  }

  // Outline button
  &.btn-outline {
    color: var(--color-primary-600);
    background-color: transparent;
    border-color: var(--color-primary-600);

    &:hover:not(:disabled) {
      color: white;
      background-color: var(--color-primary-600);
    }
  }

  // Ghost button
  &.btn-ghost {
    color: var(--color-text-secondary);
    background-color: transparent;
    border-color: transparent;

    &:hover:not(:disabled) {
      color: var(--color-text);
      background-color: var(--color-bg-secondary);
    }
  }

  // Sizes
  &.btn-sm {
    padding: map-get($spacers, 1) map-get($spacers, 3);
    font-size: map-get($font-sizes, 'sm');
  }

  &.btn-lg {
    padding: map-get($spacers, 3) map-get($spacers, 6);
    font-size: map-get($font-sizes, 'lg');
  }

  // Full width
  &.btn-block {
    width: 100%;
  }

  // With icon
  .btn-icon {
    margin-right: map-get($spacers, 2);
  }

  &.btn-icon-only {
    padding: map-get($spacers, 2);

    .btn-icon {
      margin: 0;
    }
  }

  // Loading state
  &.btn-loading {
    position: relative;
    color: transparent;
    pointer-events: none;

    &::after {
      content: '';
      position: absolute;
      width: 16px;
      height: 16px;
      top: 50%;
      left: 50%;
      margin-left: -8px;
      margin-top: -8px;
      border: 2px solid currentColor;
      border-top-color: transparent;
      border-radius: 50%;
      animation: spin 0.6s linear infinite;
    }
  }
}

// Button group
.btn-group {
  display: inline-flex;
  background-color: rgba(var(--color-primary-500-rgb), 0.1);
  border-radius: $border-radius;
  padding: 2px;

  [data-theme="dark"] & {
    background-color: rgba(var(--color-primary-500-rgb), 0.2);
  }

  .btn {
    border-radius: calc(#{$border-radius} - 2px);
    margin: 0 1px;
    background-color: transparent;
    border-color: transparent;
    color: var(--color-primary-700);

    [data-theme="dark"] & {
      color: var(--color-primary-300);
    }

    &:first-child {
      margin-left: 0;
    }

    &:last-child {
      margin-right: 0;
    }

    &:not(:first-child) {
      margin-left: 1px;
    }

    &:hover:not(:disabled),
    &:focus:not(:disabled) {
      background-color: rgba(var(--color-primary-500-rgb), 0.2);
      z-index: 1;

      [data-theme="dark"] & {
        background-color: rgba(var(--color-primary-500-rgb), 0.3);
      }
    }

    &.active {
      background-color: var(--color-primary-600);
      color: white;

      &:hover:not(:disabled) {
        background-color: var(--color-primary-700);
      }
    }
  }
}

// Floating action button
.fab {
  position: fixed;
  bottom: map-get($spacers, 6);
  right: map-get($spacers, 6);
  width: 56px;
  height: 56px;
  border-radius: $border-radius-full;
  background-color: var(--color-primary-600);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: $shadow-lg;
  transition: all $transition-base;
  z-index: map-get($z-index, 'fixed');

  &:hover {
    background-color: var(--color-primary-700);
    transform: scale(1.1);
    box-shadow: $shadow-xl;
  }

  &:active {
    transform: scale(1);
  }

  @media (max-width: map-get($breakpoints, 'sm')) {
    bottom: map-get($spacers, 4);
    right: map-get($spacers, 4);
  }
}",		"// Tag styles

.tag {
  display: inline-block;
  padding: map-get($spacers, 1) map-get($spacers, 3);
  font-size: map-get($font-sizes, 'xs');
  font-weight: map-get($font-weights, 'medium');
  color: var(--color-primary-700);
  background-color: var(--color-primary-100);
  border-radius: $border-radius-full;
  transition: all $transition-fast;

  [data-theme="dark"] & {
    color: var(--color-primary-300);
    background-color: var(--color-primary-900);
    opacity: 0.8;
  }

  &:hover {
    color: var(--color-primary-800);
    background-color: var(--color-primary-200);
    transform: translateY(-1px);

    [data-theme="dark"] & {
      color: var(--color-primary-200);
      background-color: var(--color-primary-800);
      opacity: 0.9;
    }
  }

  // Clickable tags
  &.tag-link {
    text-decoration: none;
    cursor: pointer;
  }

  // Tag sizes
  &.tag-sm {
    padding: 0 map-get($spacers, 2);
    font-size: 0.75rem;
  }

  &.tag-lg {
    padding: map-get($spacers, 2) map-get($spacers, 4);
    font-size: map-get($font-sizes, 'sm');
  }

  // Tag variants
  &.tag-secondary {
    color: var(--color-text-secondary);
    background-color: var(--color-bg-tertiary);

    &:hover {
      color: var(--color-text);
      background-color: var(--color-gray-300);
    }
  }

  &.tag-success {
    color: #15803d; // darker green
    background-color: #bbf7d0; // lighter green

    [data-theme="dark"] & {
      color: #4ade80; // lighter green for dark mode
      background-color: rgba(34, 197, 94, 0.2);
    }
  }

  &.tag-warning {
    color: #b45309; // darker orange
    background-color: #fed7aa; // lighter orange

    [data-theme="dark"] & {
      color: #fbbf24; // lighter orange for dark mode
      background-color: rgba(245, 158, 11, 0.2);
    }
  }

  &.tag-error {
    color: #b91c1c; // darker red
    background-color: #fecaca; // lighter red

    [data-theme="dark"] & {
      color: #f87171; // lighter red for dark mode
      background-color: rgba(239, 68, 68, 0.2);
