}",		"// Table styles

.doc-body {
  table {
    width: 100%;
    border-collapse: collapse;
    margin: map-get($spacers, 6) 0;
    font-size: map-get($font-sizes, 'sm');
    background-color: rgba(var(--color-primary-500-rgb), 0.02);
    border-radius: $border-radius-lg;
    overflow: hidden;
    box-shadow: $shadow;
    border: 1px solid rgba(var(--color-primary-500-rgb), 0.15);

    [data-theme="dark"] & {
      background-color: rgba(var(--color-primary-500-rgb), 0.05);
      border-color: rgba(var(--color-primary-500-rgb), 0.25);
    }

    @media (max-width: map-get($breakpoints, 'md')) {
      font-size: map-get($font-sizes, 'xs');
    }
  }

  // Table wrapper for overflow
  .table-wrapper {
    overflow-x: auto;
    margin: map-get($spacers, 6) 0;
    border-radius: $border-radius-lg;
    border: 1px solid rgba(var(--color-primary-500-rgb), 0.15);

    [data-theme="dark"] & {
      border-color: rgba(var(--color-primary-500-rgb), 0.25);
    }

    table {
      margin: 0;
      box-shadow: none;
      border-radius: 0;
    }

    &::-webkit-scrollbar {
      height: 8px;
    }
  }

  thead {
    background-color: var(--color-primary-100);
    border-bottom: 2px solid rgba(var(--color-primary-500-rgb), 0.2);

    [data-theme="dark"] & {
      background-color: rgba(var(--color-primary-500-rgb), 0.15);
      border-bottom-color: rgba(var(--color-primary-500-rgb), 0.3);
    }

    th {
      padding: map-get($spacers, 3) map-get($spacers, 4);
      font-weight: map-get($font-weights, 'semibold');
      text-align: left;
      color: var(--color-text);
      white-space: nowrap;

      &:first-child {
        border-top-left-radius: $border-radius-lg;
      }

      &:last-child {
        border-top-right-radius: $border-radius-lg;
      }
    }
  }

  tbody {
    tr {
      border-bottom: 1px solid rgba(var(--color-primary-500-rgb), 0.1);

      [data-theme="dark"] & {
        border-bottom-color: rgba(var(--color-primary-500-rgb), 0.2);
      }
      transition: background-color $transition-fast;

      &:hover {
        background-color: rgba(var(--color-primary-500-rgb), 0.05);

        [data-theme="dark"] & {
          background-color: rgba(var(--color-primary-500-rgb), 0.1);
        }
      }

      &:last-child {
        border-bottom: none;

        td:first-child {
          border-bottom-left-radius: $border-radius-lg;
        }

        td:last-child {
          border-bottom-right-radius: $border-radius-lg;
        }
      }
    }

    td {
      padding: map-get($spacers, 3) map-get($spacers, 4);
      color: var(--color-text-secondary);

      &:first-child {
        font-weight: map-get($font-weights, 'medium');
        color: var(--color-text);
      }
    }
  }

  // Striped table
  .table-striped {
    tbody tr:nth-child(odd) {
      background-color: rgba(var(--color-primary-500-rgb), 0.03);

      [data-theme="dark"] & {
        background-color: rgba(var(--color-primary-500-rgb), 0.08);
      }
    }
  }

  // Compact table
  .table-compact {
    font-size: map-get($font-sizes, 'xs');

    thead th,
    tbody td {
      padding: map-get($spacers, 2) map-get($spacers, 3);
    }
  }

  // Bordered table
  .table-bordered {
    border: 1px solid var(--color-border);

    thead th,
    tbody td {
      border: 1px solid var(--color-border);
    }
  }

  // Responsive table
  @media (max-width: map-get($breakpoints, 'md')) {
    .table-responsive {
      thead {
        display: none;
      }

      tbody {
        tr {
          display: block;
          margin-bottom: map-get($spacers, 3);
          border: 1px solid var(--color-border);
          border-radius: $border-radius;
          padding: map-get($spacers, 3);

          &:hover {
            background-color: transparent;
            border-color: var(--color-primary-500);
          }
        }

        td {
          display: block;
          padding: map-get($spacers, 2) 0;
          border: none;

          &::before {
            content: attr(data-label);
            display: block;
            font-weight: map-get($font-weights, 'semibold');
            color: var(--color-text);
            margin-bottom: map-get($spacers, 1);
          }

          &:first-child {
            padding-top: 0;
          }

          &:last-child {
            padding-bottom: 0;
          }
        }
      }
    }
  }

  // Code in tables
  table code {
    font-size: 0.9em;
  }

  // Links in tables
  table a {
    font-weight: map-get($font-weights, 'medium');
  }

  // Status badges in tables
  .badge {
    display: inline-block;
    padding: 0.125em 0.5em;
    font-size: 0.875em;
    font-weight: map-get($font-weights, 'medium');
    border-radius: $border-radius-full;

    &.badge-success {
      background-color: var(--color-success);
      color: white;
    }

    &.badge-warning {
      background-color: var(--color-warning);
      color: white;
    }

    &.badge-error {
      background-color: var(--color-error);
      color: white;
    }

    &.badge-info {
      background-color: var(--color-info);
      color: white;
    }
  }
}",		"// List styles

.doc-body {
  // Unordered lists
  ul {
    list-style: none;
    padding-left: map-get($spacers, 6);

    li {
      position: relative;
      margin-bottom: map-get($spacers, 2);

      &::before {
        content: 'â€¢';
        position: absolute;
        left: -1.5rem;
        color: var(--color-primary-500);
        font-weight: map-get($font-weights, 'bold');
      }
    }

    // Nested lists
    ul {
      margin-top: map-get($spacers, 2);
      margin-bottom: map-get($spacers, 2);

      li::before {
        content: 'â—‹';
      }

      ul li::before {
        content: 'â–ª';
        font-size: 0.8em;
      }
    }
  }

  // Ordered lists
  ol {
    list-style: none;
    padding-left: map-get($spacers, 6);
    counter-reset: list-counter;

    li {
      position: relative;
      margin-bottom: map-get($spacers, 2);
      counter-increment: list-counter;

      &::before {
        content: counter(list-counter) '.';
        position: absolute;
        left: -2rem;
        color: var(--color-primary-600);
        font-weight: map-get($font-weights, 'semibold');
        font-feature-settings: 'tnum';
      }
    }

    // Nested ordered lists
    ol {
      margin-top: map-get($spacers, 2);
      margin-bottom: map-get($spacers, 2);
      counter-reset: nested-counter;

      li {
        counter-increment: nested-counter;

        &::before {
          content: counter(list-counter, lower-alpha) '.';
        }
      }
    }
  }

  // Custom list indicators
  ul.list-check li::before { content: 'âœ“'; color: var(--color-success); }
  ul.list-arrow li::before { content: 'â†’'; }
  ul.list-star li::before { content: 'â˜…'; color: var(--color-warning); }
  ul.list-triangle li::before { content: 'â–¶'; font-size: 0.7em; }
  ul.list-square li::before { content: 'â– '; font-size: 0.8em; }
  ul.list-diamond li::before { content: 'â—†'; }

  // Task lists
  .task-list {
    list-style: none;
    padding-left: 0;

    .task-list-item {
      display: flex;
      align-items: flex-start;
      margin-bottom: map-get($spacers, 2);

      &::before {
        display: none;
      }

      input[type="checkbox"] {
        margin-right: map-get($spacers, 2);
        margin-top: 0.25em;
        cursor: pointer;

        &:checked + label {
          text-decoration: line-through;
          color: var(--color-text-tertiary);
        }
      }

      label {
        cursor: pointer;
        flex: 1;
      }
    }
  }

  // Definition lists
  dl {
    dt {
      font-weight: map-get($font-weights, 'semibold');
      color: var(--color-text);
      margin-bottom: map-get($spacers, 1);

      &::after {
        content: ':';
      }
    }

    dd {
      margin-left: map-get($spacers, 6);
      margin-bottom: map-get($spacers, 4);
      color: var(--color-text-secondary);

      &:last-child {
        margin-bottom: 0;
      }
    }
  }

  // Compact lists
  .list-compact {
    li {
      margin-bottom: map-get($spacers, 1);
    }
  }

  // Inline lists
  .list-inline {
    display: flex;
    flex-wrap: wrap;
    gap: map-get($spacers, 4);
    padding-left: 0;

    li {
      margin-bottom: 0;

      &::before {
        display: none;
      }

      &:not(:last-child)::after {
        content: 'â€¢';
        margin-left: map-get($spacers, 4);
        color: var(--color-text-tertiary);
      }
    }
  }
}",		"// Steps and process visualization styles

.steps {
  margin: map-get($spacers, 6) 0;
  position: relative;

  // Vertical steps (default)
  &:not(.steps-horizontal) {
    .step {
      display: flex;
      position: relative;
      margin-bottom: map-get($spacers, 6);

      &:last-child {
        margin-bottom: 0;

        .step-connector {
          display: none;
        }
      }
    }

    .step-indicator {
      flex-shrink: 0;
      width: 48px;
      height: 48px;
      border-radius: $border-radius-full;
      background-color: var(--color-step-bg);
      border: 3px solid rgba(var(--color-primary-500-rgb), 0.2);
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: map-get($font-weights, 'semibold');
      color: var(--color-text-secondary);
      position: relative;
      z-index: 2;
      transition: all $transition-base;

      [data-theme="dark"] & {
        background-color: var(--color-step-bg-dark);
        border-color: rgba(var(--color-primary-500-rgb), 0.3);
      }

      &.active {
        background-color: var(--color-primary-500);
        border-color: var(--color-primary-500);
        color: white;
        transform: scale(1.1);
        box-shadow: 0 0 0 4px var(--color-primary-200);
      }

      &.completed {
        background-color: var(--color-success);
        border-color: var(--color-success);
        color: white;

        .step-number {
          display: none;
        }

        &::after {
          content: 'âœ“';
          font-size: map-get($font-sizes, 'xl');
        }
      }
    }

    .step-connector {
      position: absolute;
      top: 48px;
      left: 23px;
      width: 2px;
      height: calc(100% - 48px);
      background-color: var(--color-border);
      z-index: 1;

      &.completed {
        background-color: var(--color-success);
      }
    }

    .step-content {
      flex: 1;
      padding-left: map-get($spacers, 4);

      .step-title {
        font-size: map-get($font-sizes, 'lg');
        font-weight: map-get($font-weights, 'semibold');
        margin-bottom: map-get($spacers, 2);
        color: var(--color-text);
      }

      .step-description {
        color: var(--color-text-secondary);
        margin-bottom: map-get($spacers, 3);
      }
    }
  }

  // Horizontal steps
  &.steps-horizontal {
    display: flex;
    align-items: flex-start;
    overflow-x: auto;
    padding-bottom: map-get($spacers, 2);

    &::-webkit-scrollbar {
      height: 6px;
    }

    .step {
      flex: 1;
      min-width: 200px;
      text-align: center;
      position: relative;

      &:not(:last-child) {
        &::after {
          content: '';
          position: absolute;
          top: 24px;
          left: calc(50% + 24px);
          right: calc(-50% + 24px);
          height: 2px;
          background-color: var(--color-border);
          z-index: 1;
        }

        &.completed::after {
          background-color: var(--color-success);
        }
      }
    }

    .step-indicator {
      width: 48px;
      height: 48px;
      margin: 0 auto map-get($spacers, 3);
      border-radius: $border-radius-full;
      background-color: var(--color-step-bg);
      border: 3px solid rgba(var(--color-primary-500-rgb), 0.2);
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: map-get($font-weights, 'semibold');
      color: var(--color-text-secondary);
      position: relative;
      z-index: 2;
      transition: all $transition-base;

      [data-theme="dark"] & {
        background-color: var(--color-step-bg-dark);
        border-color: rgba(var(--color-primary-500-rgb), 0.3);
      }

      &.active {
        background-color: var(--color-primary-500);
        border-color: var(--color-primary-500);
        color: white;
        transform: scale(1.1);
        box-shadow: 0 0 0 4px var(--color-primary-200);
      }

      &.completed {
        background-color: var(--color-success);
        border-color: var(--color-success);
        color: white;

        .step-number {
          display: none;
        }

        &::after {
          content: 'âœ“';
          font-size: map-get($font-sizes, 'xl');
        }
      }
    }

    .step-content {
      .step-title {
        font-size: map-get($font-sizes, 'base');
        font-weight: map-get($font-weights, 'semibold');
        margin-bottom: map-get($spacers, 1);
        color: var(--color-text);
      }

      .step-description {
        font-size: map-get($font-sizes, 'sm');
        color: var(--color-text-secondary);
      }
    }
  }

  // Compact steps
  &.steps-compact {
    .step-indicator {
      width: 32px;
      height: 32px;
      font-size: map-get($font-sizes, 'sm');
    }

    &:not(.steps-horizontal) {
      .step-connector {
        top: 32px;
        left: 15px;
        height: calc(100% - 32px);
      }
    }

    &.steps-horizontal {
      .step-indicator {
        margin-bottom: map-get($spacers, 2);
      }

      .step::after {
        top: 16px;
        left: calc(50% + 16px);
        right: calc(-50% + 16px);
      }
    }
  }
}

