        font-weight: map-get($font-weights, 'medium');
      }
    }

    // Nested levels
    ul ul {
      margin-left: map-get($spacers, 3);
      margin-top: map-get($spacers, 1);

      a {
        font-size: map-get($font-sizes, 'xs');
      }
    }
  }
}

// Content body
.doc-body {
  > * {
    margin-bottom: map-get($spacers, 4);

    &:last-child {
      margin-bottom: 0;
    }
  }

  // Headings with anchor links
  h2, h3, h4, h5, h6 {
    position: relative;
    scroll-margin-top: calc($header-height + map-get($spacers, 4));

    &:hover .heading-anchor {
      opacity: 1;
    }
  }

  .heading-anchor {
    position: absolute;
    left: -1.5rem;
    top: 50%;
    transform: translateY(-50%);
    color: var(--color-text-tertiary);
    font-size: map-get($font-sizes, 'lg');
    opacity: 0;
    transition: opacity $transition-fast;

    &:hover {
      color: var(--color-primary-600);
    }

    @media (max-width: map-get($breakpoints, 'md')) {
      display: none;
    }
  }

  // Images
  img {
    margin: map-get($spacers, 6) 0;
    border: 1px solid var(--color-border);
    border-radius: $border-radius-lg;
    box-shadow: $shadow-md;
  }

  // Tables
  table {
    margin: map-get($spacers, 6) 0;
  }

  // Lists
  ul, ol {
    margin: map-get($spacers, 4) 0;
    padding-left: map-get($spacers, 6);
  }

  // Nested lists
  li ul,
  li ol {
    margin-top: map-get($spacers, 2);
    margin-bottom: map-get($spacers, 2);
  }

  // Definition lists
  dl {
    margin: map-get($spacers, 4) 0;

    dt {
      font-weight: map-get($font-weights, 'semibold');
      margin-bottom: map-get($spacers, 1);
    }

    dd {
      margin-left: map-get($spacers, 6);
      margin-bottom: map-get($spacers, 3);
      color: var(--color-text-secondary);
    }
  }
}",		"// Page layout styles

.page-wrapper {
  padding: map-get($spacers, 6) 0;

  @media (min-width: map-get($breakpoints, 'md')) {
    padding: map-get($spacers, 8) 0;
  }

  @media (min-width: map-get($breakpoints, 'lg')) {
    padding: map-get($spacers, 10) 0;
  }
}

.page-container {
  max-width: 900px;
  margin: 0 auto;
  padding: 0 map-get($spacers, 4);

  @media (min-width: map-get($breakpoints, 'md')) {
    padding: 0 map-get($spacers, 6);
  }
}

.page-header {
  text-align: center;
  margin-bottom: map-get($spacers, 8);

  .page-title {
    margin-bottom: map-get($spacers, 3);
  }

  .page-description {
    font-size: map-get($font-sizes, 'xl');
    color: var(--color-text-secondary);
    max-width: 600px;
    margin: 0 auto;
  }
}

.page-content {
  > * {
    margin-bottom: map-get($spacers, 4);

    &:last-child {
      margin-bottom: 0;
    }
  }

  // Centered content variant
  &.page-content-centered {
    text-align: center;

    > * {
      max-width: 600px;
      margin-left: auto;
      margin-right: auto;
    }
  }
